import {useEffect, useState} from "react";
import api from "../../utils/api";
import Card from "../Card/Card";

export default function Main({onEditProfile, onEditAvatar, onAddPlace}) {
  const [userName, setUserName] = useState('')
  const [userDescription, setUserDescription] = useState('')
  const [userAvatar, setUserAvatar] = useState('')
  const [cards, setCards] = useState([])
  console.log(cards)

  useEffect(() => {
    Promise.all([api.getUserProfile(), api.getInitialCards()])
      .then(([dataProfile, dataCards]) => {
        setUserName(dataProfile.name)
        setUserDescription(dataProfile.about)
        setUserAvatar(dataProfile.avatar)
        dataCards.forEach(data => data.myid = dataProfile._id)
        setCards(dataCards)
      });
  },[])

  return (
    <main className="content">
      <section className="profile">
        <button
          className="profile__avatar-button"
          type="button"
          aria-label="Редактировать"
          onClick={onEditAvatar}
        >
          <img className="avatar" src={userAvatar} alt="Фото профиля"/>
        </button>
        <div className="profile__info">
          <h1 className="profile__name">{userName}</h1>
          <button
            className="profile__button-edit"
            type="button"
            aria-label="Редактировать"
            onClick={onEditProfile}
          />
          <p className="profile__about">{userDescription}</p>
        </div>
        <button
          className="profile__button-add"
          type="button"
          aria-label="Добавить"
          onClick={onAddPlace}
        />
      </section>
      <section className="photos">
        <template id="photo__template">
          {cards.map(data => {
            return (<Card card={data}/>)
          })}
        </template>
      </section>
    </main>
  )
}
