import {useEffect} from "react";

import PopupWithForm from "../PopupWithForm/PopupWithForm";
import useForm from "../../hooks/useForm";

export default function AddPlacePopup({isOpen, onClose, onAddPlace}) {
  const {values, setValues, handleChange, isFormValid, validity} = useForm({
    name: '',
    link: ''
  })

  useEffect(() => {
    setValues({
      name: '',
      link: ''
    })

  }, [setValues, isOpen])

  function handleSubmit(e) {
    e.preventDefault()

    onAddPlace({
      name: values.name,
      link: values.link
    })
  }

  return (
    <PopupWithForm
      name='add-place'
      title='Новое место'
      titleButton='Создать'
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
      isValid={isFormValid}
    >
      <input
        required={true}
        name="name"
        className={`popup__input ${validity.name && "popup__input_error"}`}
        type="text"
        placeholder="Название"
        minLength={2}
        maxLength={30}
        value={values.name}
        onChange={handleChange}
      />
      <span className={`popup__error ${validity.name && "popup__error_visible"}`} name="name-error">{validity.name && validity.name}</span>
      <input
        required={true}
        name="link"
        className={`popup__input ${validity.link && "popup__input_error"}`}
        type="url"
        placeholder="Ссылка на картинку"
        value={values.link}
        onChange={handleChange}
      />
      <span className={`popup__error ${validity.link && "popup__error_visible"}`} name="link-error">{validity.link && validity.link}</span>
    </PopupWithForm>
  )
}
