import {useState} from "react";

export default function useForm(inputValues) {
  const initValidity = {}
  const requiredInputs = []
  const initValues = {}

  Object.keys(inputValues).forEach((key) => {
    initValidity[key] = null
    initValues[key] = ''

    if (inputValues[key]) {
      requiredInputs.push(key)
    }
  })

  const [values, setValues] = useState(initValues)
  const [isFormValid, setIsFormValid] = useState(false)
  const [validity, setValidity] = useState(initValidity)
  const [validationMessages, setValidationMessages] = useState(initValidity)

  const handleChange = (event) => {
    const {value, name, validationMessage} = event.target
    const isValid = event.target.checkValidity()
    const newValidity = {...validity, [name]: isValid ? true : validationMessage}
    const newIsFormValid = Object.keys(newValidity).every((key) => {
      return (newValidity[key] === true && requiredInputs.includes(name)) || newValidity[key] === null
    })

    setValues({...values, [name]: value})
    setValidity(newValidity)
    setIsFormValid(newIsFormValid)
  }

  const resetForm = (resetValues) => {
    resetValues = resetValues || {}
    const newValidity = {}
    const newValues = {}

    Object.keys(inputValues).forEach((key) => {
      newValidity[key] = null
    })

    Object.keys(inputValues).forEach((key) => {
      newValues[key] = resetValues[key] || ''
    })

    setValidity(newValidity)
    setValues(newValues)
  }

  return {values, handleChange, isFormValid, validity, resetForm}
}
