import {useEffect} from "react";

import PopupWithForm from "../PopupWithForm/PopupWithForm";
import useForm from "../../hooks/useForm";
import PopupInput from "../PopupInput/PopupInput"
export default function AddPlacePopup({isOpen, onClose, onAddPlace}) {
  const {values, resetForm, handleChange, isFormValid, validity, validationMessages} = useForm({
    name: true,
    link: true
  })

  useEffect(() => {
    resetForm()
  }, [isOpen])

  function handleSubmit(e) {
    e.preventDefault()

    onAddPlace({
      name: values.name,
      link: values.link
    })
  }

  return (
    <PopupWithForm
      name='add-place'
      title='Новое место'
      titleButton='Создать'
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
      isValid={isFormValid}
    >
      <PopupInput
        required={true}
        name="name"
        type="text"
        placeholder="Название"
        minLength={2}
        maxLength={30}
        value={values.name}
        onChange={handleChange}
        isValid={validity.name}
        validationMessage={validationMessages.name}
      />
      <PopupInput
        required={true}
        name="link"
        type="url"
        placeholder="Ссылка на картинку"
        value={values.link}
        onChange={handleChange}
        isValid={validity.link}
        validationMessage={validationMessages.link}
      />
    </PopupWithForm>
  )
}
