import {useState} from "react";

export default function useForm() {
  const [values, setValues] = useState({})
  const [validity, setValidity] = useState({})
  const [isFormValid, setIsFormValid] = useState(false)
  const [required, setRequired] = useState([])

  const handleChange = (event) => {
    const {value, name, validationMessage} = event.target
    const isValid = event.target.checkValidity()
    const newValidity = {...validity, [name]: isValid ? null : validationMessage}
    const newIsFormValid = Object.values(newValidity).every(v => v === null)

    setValues({...values, [name]: value})
    setValidity(newValidity)
    setIsFormValid(newIsFormValid)
  }

  const resetForm = (resetValues) => {
    resetValues = resetValues || {}
    const newValidity = {}
    const newValues = {}

    Object.keys(inputValues).forEach((key) => {
      newValidity[key] = null
    })

    Object.keys(inputValues).forEach((key) => {
      newValues[key] = resetValues[key] || ''
    })

    setValidity(newValidity)
    setValues(newValues)
  }

  const register = ({ name, required }) => {
    setValues({...values, [name]: ''})
    if (required) {
      setRequired([...required, name])
    }
  }

  return {values, handleChange, isFormValid, validity, resetForm, register}
}
