import {Link, useOutletContext} from "react-router-dom";
import {useState} from "react";
import authApi from "../../utils/authApi";

export default function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { setMenu } = useOutletContext()

  setMenu(<Link className="login__subtitle-link" to="/sign-in">Войти</Link>)

  function handleSubmit(e) {
    e.preventDefault()

    authApi.signup(email, password).then(() => {
      alert("Вы успешно зарегистрированы")
    }).catch(console.error)
  }

  return (<>
    <section className="login login__page">
      <h2 className="login__title">Регистрация</h2>
      <form
        className="login__form"
        name="register-form"
        onSubmit={handleSubmit}
      >
        <input
          required=""
          name="email"
          className="login__input"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          required=""
          name="password"
          className="login__input"
          type="password"
          placeholder="Пароль"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button
          className="login__button"
          type="submit"
          aria-label="Зарегистрироваться"
        >
          Зарегистрироваться
        </button>
      </form>
      <p className="login__subtitle">Уже зарегистрированы?
        <Link className="login__subtitle-link" to="/sign-in"> Войти</Link>
      </p>
    </section>
  </>)
}
