import {Link, useOutletContext} from "react-router-dom";
import {useEffect, useState} from "react";
import authApi from "../../utils/authApi";
import Popup from "../Popup/Popup";
import fail from "../../images/fail.svg"
import success from "../../images/success.svg"


export default function Register() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [isRegistrationErrorPopupOpen, setIsRegistrationErrorPopupOpen] = useState(false)
  const [isRegistrationSuccessPopupOpen, setIsRegistrationSuccessPopupOpen] = useState(false)

  const { setMenu } = useOutletContext()

  useEffect(() => {
    setMenu([
      <div className="header__info">
      <Link className="header__sign" to="/sign-in">Войти</Link>
      </div>
    ])
  }, [setMenu])

  function handleSubmit(e) {
    e.preventDefault()

    authApi.signup(email, password).then(() => {
      setIsRegistrationSuccessPopupOpen(true)
    }).catch(() => {
      setIsRegistrationErrorPopupOpen(true)
    })
  }

  return (<>
    <Popup name="onRegistrationError"
           isOpen={isRegistrationErrorPopupOpen}
           onClose={() => setIsRegistrationErrorPopupOpen(false)}>
      <img
        className="c"
        src={fail}
        />
      <h2>Что-то пошло не так! Попробуйте ещё раз.</h2>
    </Popup>

    <Popup name="onRegistrationSuccess"
           isOpen={isRegistrationSuccessPopupOpen}
           onClose={() => setIsRegistrationSuccessPopupOpen(false)}>
      <img src={success}/>
      <h2>Вы успешно зарегистрировались!</h2>
    </Popup>

    <section className="login login__page">
      <h2 className="login__title">Регистрация</h2>
      <form
        className="login__form"
        name="register-form"
        onSubmit={handleSubmit}
      >
        <input
          required=""
          name="email"
          className="login__input"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          required=""
          name="password"
          className="login__input"
          type="password"
          placeholder="Пароль"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button
          className="login__button"
          type="submit"
          aria-label="Зарегистрироваться"
        >
          Зарегистрироваться
        </button>
      </form>
      <p className="login__subtitle">Уже зарегистрированы?
        <Link className="login__subtitle-link" to="/sign-in"> Войти</Link>
      </p>
    </section>
  </>)
}
