              className="popup__input"
              type="text"
              placeholder="О себе"
              minLength={2}
              maxLength={200}
          />
          <span className="popup__error" name="about-error" />
        </PopupWithForm>

        <PopupWithForm
            name='add-photo'
            title='Новое место'
            titleButton='Создать'
