              name="name"
              className="popup__input"
              type="text"
              placeholder="Название"
              minLength={2}
              maxLength={30}
          />
          <span className="popup__error" name="name-error" />
          <input
              required=""
              name="link"
              className="popup__input"
              type="url"
              placeholder="Ссылка на картинку"
          />
          <span className="popup__error" name="link-error" />
        </PopupWithForm>

        <PopupWithForm
            name='edit-avatar'
            title='Обновить аватар'
        >
          <input
              required=""
              name="avatar"
              className="popup__input"
              type="url"
              placeholder="Ссылка на картинку"
          />
          <span className="popup__error" name="avatar-error" />
        </PopupWithForm>

        <PopupWithForm
            name='delete-photo'
            title='Вы уверены?'
            titleButton='Да'
        />

        <PopupImage/>
      </div>
  );
}

export default App;
