import {useState} from "react";

export default function useForm(inputValues) {
  const initValidity = {}
  Object.keys(inputValues).forEach((key) => {
    initValidity[key] = ""
  })

  const [values, setValues] = useState(inputValues)
  const [isFormValid, setIsFormValid] = useState(false)
  const [validity, setValidity] = useState(initValidity)

  const handleChange = (event) => {
    const {value, name, validationMessage} = event.target
    const isValid = event.target.checkValidity()

    setValues({...values, [name]: value})
    setValidity({...validity, [name]: isValid ? "" : validationMessage})
    setIsFormValid(validity.some(item => !item.validity.valid))
  }

  return {values, setValues, handleChange, isFormValid, validity}
}
