import Api from "./Api"

class AuthApi extends Api {
  constructor(options) {
    super(options)
  }
  signup(email, password) {
    return this._makeRequest("/signup", {
      method: "POST",
      body: {
        password: password,
        email: email
      }
    })
  }
}

const authApi = new AuthApi({
  baseUrl: "https://auth.nomoreparties.co"
})
export default authApi
