import {useEffect, useState} from "react";
import {BrowserRouter, Routes, Route, Navigate} from "react-router-dom";
import Index from "./Index/Index";
import ProtectedRoute from "./ProtectedRoute/ProtectedRoute";
import Login from "./Login/Login";
import Register from "./Register/Register";
import PageLayout from "./PageLayout/PageLayout";
import authApi from "../utils/authApi";

export default function App() {
  const [userToken, setUserToken] = useState(localStorage.getItem("userToken"))
  const [authenticatedUser, setAuthenticatedUser] = useState('')

  useEffect(() => {
    if (userToken) {
      authApi.getCurrentUser(userToken)
        .then((user) => setAuthenticatedUser(user))
        .catch(console.error)
    }
  }, [userToken])

  function handleSignIn({ token }) {
    localStorage.setItem("userToken", token)
    setUserToken(token)
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route element={<PageLayout/>}>
          <Route path="/sign-in" element={<Login onSignin={handleSignIn}/>}/>
          <Route path="/sign-up" element={<Register/>}/>
          {/*<Route index path="/" element={*/}
            <ProtectedRoute user={authenticatedUser} redirectPath={"/sign-up"}>
              <Index/>
            </ProtectedRoute>
          }/>
          {/*<Route path="*" element={<Navigate to="/" replace/>}/>*/}
        </Route>
      </Routes>
    </BrowserRouter>
  )
}
